# Loop over input items and add a new field called 'myNewField' to the JSON of each one
for item in _input.all():
  item.json.myNewField = 1
return _input.all()

# Wrap all extracted page info into {url, data}
out = []
for it in items:
    j = it.get("json", {}) or {}

    # ה־mainUrl הוא הכתובת של הדף שחפרנו עליו
    u = j.get("mainUrl") or j.get("url") or ""

    # נזרוק את ה-mainUrl מתוך data כדי לא לשכפל
    data_copy = {k: v for k, v in j.items() if k not in {"mainUrl", "url"}}

    out.append({
        "json": {
            "url": u,
            "data": data_copy
        }
    })

return out
