function parseDate(input) {
  try {
    return JSON.parse(input);
  } catch {
    return null;
  }
}

function formatDate(obj) {
  if (!obj) return "";

  if (obj.dateTime) {
    const dateObj = new Date(obj.dateTime);

    const optionsDate = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    const optionsTime = { hour: '2-digit', minute: '2-digit', hour12: false };

    const date = new Intl.DateTimeFormat('he-IL', optionsDate).format(dateObj);
    const time = new Intl.DateTimeFormat('he-IL', optionsTime).format(dateObj);

    return `${date} ×‘×©×¢×” ${time}`;
  }

  if (obj.date) {
    const dateObj = new Date(obj.date);

    const optionsDate = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return new Intl.DateTimeFormat('he-IL', optionsDate).format(dateObj);
  }

  return "";
}

