function escapeMarkdownV2(text) {
  if (!text) return "";
  return text.replace(/([_*\[\]()~`>#+\-=|{}.!\\])/g, '\\$1');
}

function getDateFromRaw(raw) {
  if (!raw) return null;
  const value = raw.dateTime || raw.date;
  return value ? new Date(value) : null;
}

function getDateKey(raw) {
  const dateObj = getDateFromRaw(raw);
  return dateObj ? dateObj.toISOString().split("T")[0] : null;
}

function formatDayLabel(dateKey) {
  if (!dateKey) return "";
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);

  const dateObj = new Date(dateKey);
  dateObj.setHours(0, 0, 0, 0);

  if (dateObj.getTime() === today.getTime()) return "×”×™×•×";
  if (dateObj.getTime() === tomorrow.getTime()) return "××—×¨";

  const options = { weekday: "long", day: "numeric", month: "long", year: "numeric" };
  return new Intl.DateTimeFormat("he-IL", options).format(dateObj);
}

function formatTime(raw) {
  if (!raw || !raw.dateTime) return null;
  const dateObj = new Date(raw.dateTime);
  const options = { hour: "2-digit", minute: "2-digit", hour12: false };
  return new Intl.DateTimeFormat("he-IL", options).format(dateObj);
}

function createLink(name, url) {
  const safeName = escapeMarkdownV2(name);
  const safeUrl = url.replace(/\)/g, "\\)").replace(/\(/g, "\\(");
  return `[${safeName}](${safeUrl})`;
}

