function calculateHeatIndex(tempC, humidity) {
  const T = tempC;
  const RH = humidity;
  return -8.784695 +
         1.61139411*T +
         2.338549*RH +
         -0.14611605*T*RH +
         -0.012308094*T*T +
         -0.016424828*RH*RH +
         0.002211732*T*T*RH +
         0.00072546*T*RH*RH +
         -0.000003582*T*T*RH*RH;
}

function getHeatStressLevel(hi) {
  if (hi < 27) return "×§×œ";
