return items.map(item => {
  const pubDate = item.json.pubDate;
  let timestamp = null;

  try {
    timestamp = new Date(pubDate).getTime();
  } catch (e) {
    timestamp = null; 
  }

  return {
    json: {
      ...item.json,
      timestamp 
    }
  };
});

