// Force RTL with Unicode marks
const RLM = '\u200F';
const RLE = '\u202B';
const PDF = '\u202C';

const now = new Date();
const datePart = now.toLocaleDateString('he-IL', {
  weekday: 'long',
  day: '2-digit',
  month: '2-digit',
  year: 'numeric',
});
const timePart = now.toLocaleTimeString('he-IL', {
  hour:   '2-digit',
  minute: '2-digit',
});

// Start RTL block: flag on its own line, then date, then time
let message = RLE
  + `🇮🇱\n`
  + `${datePart}\n`
  + `שעה: ${timePart}\n\n`;

$input.all().forEach(item => {
  const a = item.json;
  message += RLM + `${a.index}. ${a.title}\n`;
  message += RLM + `${a.content}\n`;
  message += RLM + `<a href="${a.link}">לכתבה המלאה</a>\n\n`;
});

message += PDF;

return [{ json: { message } }];

