return items.map(item => {
  const rawText = item.json.text || '';

  const cleanText = rawText
    .replace(/\b\d{2}\/\d{2}\/\d{4}\b/g, '')
    .replace(/^\s*\d{1,3}\s*$/gm, '')
    .replace(/[ \t]+/g, ' ')
    .replace(/\n{2,}/g, '\n\n') 
    .replace(/\n\s*/g, ' ')
    .replace(/\s{2,}/g, ' ')
    .trim();

  return {
    json: {
      ...item.json,
      cleanText
    }
  };
});
